'use strict';

module.exports = function (source) {
    this.cacheable && this.cacheable();
    const parsedJSON = JSON.parse(source);
    return JSON.stringify(deleteNumericProp(parsedJSON));
};

function deleteNumericProp(obj) {
    if (typeof obj === 'object') {
        for (let key in obj) {
            if (!(obj instanceof Array) && typeof obj[key] === 'number') {
                delete obj[key];
            } else {
                obj[key] = deleteNumericProp(obj[key]);
            }
        }
    }
    return obj;
}