'use strict';

module.exports = source => {
    const parsedJSON = JSON.parse(source);
    const exportedStr = JSON.stringify(deleteNumericProp(parsedJSON));
    return `module.exports = ${exportedStr};`;
};

function deleteNumericProp(obj) {
    if (typeof obj === 'object') {
        for (let key in obj) {
            if (!(obj instanceof Array) && typeof obj[key] === 'number') {
                delete obj[key];
            } else {
                obj[key] = deleteNumericProp(obj[key]);
            }
        }
    }
    return obj;
}